class Toast {
  parent = document.body;
  wrap = null;
  iconMap;
  isHiding = false;
  options;
  constructor() {
    this.iconMap = {
      success: "data:image/svg+xml;base64,",
      error: "data:image/svg+xml;base64,",
      warning: "data:image/svg+xml;base64,",
      custom: "",
      none: ""
    };
    this.initOptions();
  }
  initOptions() {
    this.options = {
      icon: "none",
      mask: false,
      duration: 2e3,
      maxWidth: "auto",
      place: "center",
      offset: "10px",
      layout: "block",
      animation: "fade"
    };
  }
  show(options) {
    if (this.wrap) {
      this.hide(() => {
        this.show(options);
      });
      return;
    }
    const { animation, place } = options;
    if (!animation && place && place !== "center") {
      const animationMap = {
        top: "down",
        bottom: "up"
      };
      options.animation = animationMap[place];
    }
    Object.assign(this.options, options);
    const { title, iconUrl, parent, duration } = options;
    if (iconUrl) {
      this.iconMap.custom = iconUrl;
      this.options.icon = "custom";
    }
    if (this.options.icon === "none" && !title) {
      console.error("icon or title is required!");
      return;
    }
    if (parent) {
      this.parent = parent;
    }
    this.generateHTML();
    if (duration) {
      const timer = setTimeout(() => {
        this.hide();
        clearTimeout(timer);
      }, duration);
    }
  }
  hide(callback) {
    if (!this.wrap || this.isHiding) {
      return;
    }
    this.isHiding = true;
    const { animation } = this.options;
    const mainNode = this.wrap.querySelector(".global-api-toast-main");
    const animationendHandler = () => {
      mainNode?.removeEventListener("animationend", animationendHandler);
      this.parent.removeChild(this.wrap);
      this.wrap = null;
      this.isHiding = false;
      this.initOptions();
      callback?.();
    };
    mainNode?.addEventListener("animationend", animationendHandler);
    mainNode?.classList.add(`global-api-${animation}-out`);
  }
  generateHTML() {
    const { place, offset, layout, mask, icon, maxWidth, title, wrapClass } = this.options;
    const { animation } = this.options;
    this.wrap = document.createElement("div");
    this.wrap.classList.add("global-api-toast");
    if (wrapClass) {
      this.wrap.classList.add(wrapClass);
    }
    let mainClass = `global-api-toast-main global-api-${animation}-in`;
    if (layout === "inline") {
      mainClass += ` global-api-toast-main--inline`;
    }
    let placeStyle = "top: 50%; ";
    console.log("place", place);
    if (place === "top") {
      placeStyle = `top: ${offset};`;
      mainClass += ` global-api-toast-main--top`;
    }
    if (place === "bottom") {
      placeStyle = `bottom: ${offset};`;
      mainClass += ` global-api-toast-main--bottom`;
    }
    const maskHTML = mask ? '<div class="global-api-toast-overlay"></div>' : "";
    const mainHTMlBegin = `<div class="${mainClass}" style="max-width: ${maxWidth}; ${placeStyle}">`;
    const iconHTML = icon === "none" ? "" : `<div class="global-api-toast-icon" style="background-image: url(${this.iconMap[icon]})"></div>`;
    const titleHTML = title ? `<div class="global-api-toast-title">${title}</div>` : "";
    const mainHTMlEnd = "</div>";
    const innerHTML = maskHTML + mainHTMlBegin + iconHTML + titleHTML + mainHTMlEnd;
    this.wrap.innerHTML = innerHTML;
    this.parent.appendChild(this.wrap);
  }
}

export { Toast };
